mapper.select:
select c.*, u.username as author_name, t.apply_status, cm.company_name from news_content c left join news_user u on c.author_id = u.id left join news_top t on t.news_id = c.id left join news_company cm on cm.id = c.company_id where c.company_id = #{companyId}

	and c.news_type = #{newsType}


	and c.sendtime >= date_format(now(),'%Y-%m-%d')

 order by c.sendtime desc, c.id desc limit 0, 10

select t.* from ((select c.*, u.username as author_name, t.apply_status, cm.company_name from news_content c left join news_user u on c.author_id = u.id left join news_top t on t.news_id = c.id left join news_company cm on cm.id = c.company_id where c.company_id = #{companyId}

	and c.news_type = #{newsType}

and c.sendtime = (select nc.sendtime from news_content nc where nc.id = #{newsId}) and c.id < #{newsId}

	and c.sendtime >= date_format(now(),'%Y-%m-%d')

order by c.sendtime desc, c.id desc)
union all
(select c.*, u.username as author_name, t.apply_status, cm.company_name from news_content c left join news_user u on c.author_id = u.id left join news_top t on t.news_id = c.id left join news_company cm on cm.id = c.company_id where c.company_id = #{companyId}

	and c.news_type = #{newsType}

and c.sendtime < (select nc.sendtime from news_content nc where nc.id = #{newsId})

	and c.sendtime >= date_format(now(),'%Y-%m-%d')

order by c.sendtime desc, c.id desc)) t order by t.sendtime desc, t.id desc limit 0, 10
